# nombre de handle contenant une majuscule

def gotmaj
    puts "Il y a #{journalists.count { |x| x =~ /[A-Z]/ }} handles qui contiennent une majuscule."
end

puts gotmaj

# nombre de handle avec "underscore" dedans

def undescore
    puts "Il y a #{journalists.sum { |s| s.count('_') }} handles avec un underscore."
end

puts undescore

